mapper.select:
SELECT ROW_NUMBER() OVER (ORDER BY dpdtjyy DESC) num, dpdtjyy, COUNT(*) AS dt, SUM(CASE jycs WHEN 1 THEN 1 ELSE 0 END) AS jc1, SUM(CASE jycs WHEN 2 THEN 1 ELSE 0 END) AS jc2, SUM(CASE jycs WHEN 3 THEN 1 ELSE 0 END) AS jc3, SUM(CASE jycs WHEN 4 THEN 1 ELSE 0 END) AS jc4 FROM pda_db.dbo.ac_data_dyn
dpdtjyy IS NOT NULL
and dpdtjyy like concat('%', #{dpdtjyy },'%')
and '20' + SUBSTRING(jylsh, 8, 6) >= #{startData}
and '20' + SUBSTRING(jylsh, 8, 6) <= #{stopData}
GROUP BY dpdtjyy
