mapper.select:
SELECT DISTINCT ROW_NUMBER() OVER (ORDER BY wjy DESC) id, wjy as name, COUNT(1) AS ciS, SUM(CASE jycs WHEN 1 THEN 1 ELSE 0 END) AS jc1, SUM(CASE jycs WHEN 2 THEN 1 ELSE 0 END) AS jc2, SUM(CASE jycs WHEN 3 THEN 1 ELSE 0 END) AS jc3, SUM(CASE jycs WHEN 4 THEN 1 ELSE 0 END) AS jc4 FROM pda_db.dbo.AC_DATA_WG AS w
wjy IS NOT NULL
and wjy like concat('%', #{wjy },'%')
and '20' + SUBSTRING(jylsh, 8, 6) >= #{startData}
and '20' + SUBSTRING(jylsh, 8, 6) <= #{stopData}
GROUP BY wjy
